$.noConflict();
jQuery(document).ready(function($){
    var highlight_target,
        keys = {
            isShift: false,
            isCtrl: false,
            isAlt: false
        },
        host = '//helpdesk.zeetway.net/api/ticket.json',
        css = '//helpdesk.zeetway.net/api/widget.css',
        locale_ru = {
            btn_close: 'Закрыть',
            btn_back: 'Назад',
            tooltip_title: 'Отчет об ошибке',
            tooltip_screenshot: 'Сделать скриншот',
            tooltip_highlight: 'Выделить область',
            tooltip_submit: 'Отправить отчет',
            textarea_placeholder: 'Пожалуйста, расскажите нам об ошибке или проблеме, которую Вы нашли, постарайтесь описать наиболее детально.',
            result_done_title: 'Спасибо',
            result_done_text: 'Ваш отчет успешно отправлен, мы постараемся обработать его в ближайшее время.',
            result_wait_title: 'Пожалуйста<br>подождите...',
            result_fail_title: 'Ошибка',
            result_fail_text: 'Упсс... что-то пошло не так, попробуйте повторить запрос.'
        };

    var $body = $(document.body),
        $style = $('<link rel="stylesheet" href="'+css+'">'),
        $feedback = $('<div class="feedback right ooops"></div>'),
        $tooltips = $('<div class="tooltips"><div class="tooltips-grp dropup"></div></div>'),
        $tooltips_grp = $tooltips.find('.tooltips-grp');

    $tooltips_grp.append('<button class="feedback-bug" type="button">');
    $tooltips_grp.append('<div class="feedback-modal feedback-modal-right">' +
        '<div class="feedback-report">' +
            '<div class="feedback-title">'+locale_ru.tooltip_title+'</div>' +
            '<form action="#">' +
                '<div class="feedback-row">' +
                    '<input class="form-control form-control-email" type="text" placeholder="Введите email" value="" name="email" />' +
                    '<textarea name="comment"  placeholder="'+locale_ru.textarea_placeholder+'"></textarea>' +
                    '<input type="hidden" class="screen-uri" name="screenshot" />' +
                    '<span class="tooltips-action screenshot cam" title="'+locale_ru.tooltip_screenshot+' (Ctrl+Alt+S)"></span>' +
                    '<span class="tooltips-action highlight" title="'+locale_ru.tooltip_highlight+' (Ctrl+Alt+H)"></span>' +
                '</div>' +
                '<div class="feedback-row clearfix"><button class="feedback-btn feedback-submit" type="button">'+locale_ru.tooltip_submit+'</button></div>' +
            '</form>' +
        '</div>' +
        '<div class="feedback-process hideme"><div class="feedback-title">'+locale_ru.result_wait_title+'</div><p><span class="feedback-spin feedback-rotate feedback-process-spin-big"></span></p></div>' +
        '<div class="feedback-done hideme"><div class="feedback-title">'+locale_ru.result_done_title+'</div><p>'+locale_ru.result_done_text+'</p><div class="feedback-row"><button class="feedback-btn feedback-btn-success feedback-close" type="button">'+locale_ru.btn_close+'</button></div></div>' +
        '<div class="feedback-fail hideme"><div class="feedback-title">'+locale_ru.result_fail_title+'</div><p>'+locale_ru.result_fail_text+'</p><div class="feedback-row"><button class="feedback-btn feedback-btn-success feedback-back" type="button">'+locale_ru.btn_back+'</button></div></div>' +
    '</div>');

    $feedback.on('click', '.feedback-bug', function(e) {
        e.preventDefault();
        $(e.target).parent().toggleClass('open');
    });

    $feedback.on('click', '.feedback-close', function(e) {
        e.preventDefault();

        $feedback.find('.tooltips-grp').removeClass('open');
        $feedback.find('.feedback-process, .feedback-fail, .feedback-done').addClass('hideme');
        $feedback.find('.feedback-report').removeClass('hideme');
        $feedback.find('.screenshot').removeClass('process success').addClass('cam');
        $feedback.find('textarea, .screen-uri').val('');
    });

    $feedback.on('click', '.feedback-back', function(e) {
        e.preventDefault();

        $feedback.find('.feedback-fail').addClass('hideme');
        $feedback.find('.feedback-report').removeClass('hideme');
    });

    $feedback.on('click', '.feedback-submit', function(e) {
        e.preventDefault();

        $feedback.find('.feedback-report').addClass('hideme');
        $feedback.find('.feedback-process').removeClass('hideme');

        unbindSelectionActions();
        cleanSelection( $('body'), true );

        $.ajax({
            url: host,
            type: 'post',
            data: $feedback.find('form').serialize(),
            dataType: 'json',
            success: function() {
                $feedback.find('.feedback-process').addClass('hideme');
                $feedback.find('.feedback-done').removeClass('hideme');
            },
            error: function() {
                $feedback.find('.feedback-process').addClass('hideme');
                $feedback.find('.feedback-fail').removeClass('hideme');
            }
        });
    });

    //Additional actions
    $(document).keyup(function(e){
        if (e.which == 16 ) {keys.isShift = false}
        if (e.which == 17 ) {keys.isCtrl = false}
        if (e.which == 18 ) {keys.isAlt = false}
    });
    $(document).keydown(function(e){
        if (e.which == 16 ) {keys.isShift = true}
        if (e.which == 17 ) {keys.isCtrl = true}
        if (e.which == 18 ) {keys.isAlt = true}

        if ( e.which == 83 && keys.isCtrl && keys.isAlt ) {
            makeScreenshot();
        }
    });
    $feedback.on('click', '.screenshot', makeScreenshot );
    $feedback.on('click', '.highlight', function(e){
        if ( $(e.target).hasClass('active') ) {
            unbindSelectionActions();
            cleanSelection($feedback);
        } else {
            bindSelectActions();
        }
    });

    //Render
    $feedback.append($style).append($tooltips);
    $body.append($feedback);

    setTimeout(function(){
        $feedback.removeClass('ooops');
    }, 2000);

    //Private
    function bindSelectActions() {
        $feedback.find('.highlight').addClass('active');
        $(document).on('mouseover', 'body', selectArea);
        $(document).on('click', '.feedback-highlight', setSelectArea);
    }
    function unbindSelectionActions() {
        $feedback.find('.highlight').removeClass('active');
        $(document).off('mouseover', 'body', selectArea);
        $(document).off('click', '.feedback-highlight', setSelectArea);
    }
    function selectArea(e) {
        if ( e.target === document.body || (highlight_target && highlight_target === e.target) ) {
            return;
        }

        if ( highlight_target ) {
            $(highlight_target).removeClass('feedback-highlight');
            highlight_target = undefined;
        }

        if ( e.target ) {
            highlight_target = e.target;
            $(highlight_target).addClass('feedback-highlight');
        }
    }

    function setSelectArea(e) {
        e.preventDefault();
        $(e.target).toggleClass('feedback-highlight-set');
    }

    function cleanSelection(obj, all) {
        obj.find('.feedback-highlight').removeClass('feedback-highlight');
        if ( typeof all !== 'undefined') {
            obj.find('.feedback-highlight-set').removeClass('feedback-highlight-set');
        }
    }

    function makeScreenshot() {
        unbindSelectionActions();
        cleanSelection($feedback);

        var $self = $feedback.find('.screenshot');

        $self.removeClass('cam').addClass('process');
        $feedback.find('.feedback-modal').addClass('hideme');
        $feedback.find('.feedback-bug').addClass('process');
        html2canvas( document.body, {
            onrendered: function(canvas) {
                $feedback.find('.screen-uri').val(canvas.toDataURL("image/png"));
                $self.removeClass('process').addClass('success');
                $feedback.find('.feedback-modal').removeClass('hideme');
                $feedback.find('.feedback-bug').removeClass('process');
            }
        });
    }
});
